import javax.xml.parsers.DocumentBuilderFactory;

public class XMLParser {

    // Method to parse XML and access float attribute
    public static float getFloatAttributeValue(String tagName, String attributeName, String xml) throws Exception {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();
        Document doc = builder.parse(xml);

        NodeList nodeList = doc.getElementsByTagName(tagName);
        if (nodeList.getLength() > 0) {
            Element element = (Element) nodeList.item(0);
            String attributeValue = element.getAttribute(attributeName);
            float floatValue = Float.parseFloat(attributeValue);
            return floatValue;
        }

        throw new IllegalArgumentException("Tag name not found in XML: " + tagName);
    }

    // Example usage
    public static void main(String[] args) {
        String xml = "<data><tag1 attribute1=\"123.45\"/><tag2 attribute2=\"678.90\"/></data>";
        try {
            float floatValue = getFloatAttributeValue("tag1", "attribute1", xml);
            System.out.println("Float attribute value: " + floatValue);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

