import java.util.HashMap;
import java.util.Map;

public class IdentifierCache {
    private static final long DEFAULT_CACHE_TIME_MS = 3000; // Default cache time in milliseconds
    private Map<String, Long> cache; // Map to store identifiers and their cached times

    // Constructor
    public IdentifierCache() {
        cache = new HashMap<>();
    }

    // Add an identifier to the cache with default caching time
    public void addIdentifier(String identifier) {
        addIdentifier(identifier, DEFAULT_CACHE_TIME_MS);
    }

    // Add an identifier to the cache with specified caching time
    public void addIdentifier(String identifier, long cacheTimeMs) {
        long currentTimeMs = System.currentTimeMillis();
        long expirationTimeMs = currentTimeMs + cacheTimeMs;
        cache.put(identifier, expirationTimeMs);
    }

    // Check if an identifier is cached and not expired
    public boolean isIdentifierCached(String identifier) {
        Long expirationTimeMs = cache.get(identifier);
        if (expirationTimeMs == null) {
            return false; // Identifier not found in cache
        } else {
            long currentTimeMs = System.currentTimeMillis();
            return expirationTimeMs > currentTimeMs; // Return true if not expired
        }
    }

    public static void main(String[] args) {
        // Create an instance of IdentifierCache
        IdentifierCache identifierCache = new IdentifierCache();

        // Add an identifier to the cache
        String identifier = "123456";
        identifierCache.addIdentifier(identifier);

        // Check if the identifier is cached and not expired
        boolean isCached = identifierCache.isIdentifierCached(identifier);
        System.out.println("Identifier " + identifier + " is cached: " + isCached);
    }
}

