import java.util.*;

public class CSP {
    private int[][] constraints; // Constraints matrix
    private List<Integer>[] domains; // Domains of variables

    // Constructor
    public CSP(int[][] constraints, List<Integer>[] domains) {
        this.constraints = constraints;
        this.domains = domains;
    }

    // Make the CSP arc-consistent
    public void makeArcConsistent() {
        Queue<Pair<Integer, Integer>> queue = new LinkedList<>(); // Queue for arc consistency propagation

        // Initialize the queue with all arcs
        for (int i = 0; i < constraints.length; i++) {
            for (int j = 0; j < constraints.length; j++) {
                if (constraints[i][j] != 0) {
                    queue.offer(new Pair<>(i, j));
                }
            }
        }

        // Perform arc consistency propagation
        while (!queue.isEmpty()) {
            Pair<Integer, Integer> arc = queue.poll();
            int i = arc.getKey();
            int j = arc.getValue();
            boolean revised = revise(i, j);
            if (revised) {
                // If domain of variable i is revised, add all arcs (i, k) to the queue for consistency propagation
                for (int k = 0; k < constraints.length; k++) {
                    if (constraints[i][k] != 0 && k != j) {
                        queue.offer(new Pair<>(i, k));
                    }
                }
            }
        }
    }

    // Revise the domain of variable i with respect to the constraint between variables i and j
    private boolean revise(int i, int j) {
        boolean revised = false;
        Iterator<Integer> iterator = domains[i].iterator();
        while (iterator.hasNext()) {
            int valueI = iterator.next();
            boolean consistent = false;
            for (int valueJ : domains[j]) {
                if (constraints[i][j] == 0 || constraints[i][j] == valueI - valueJ) {
                    consistent = true;
                    break;
                }
            }
            if (!consistent) {
                iterator.remove(); // Remove value from domain of variable i
                revised = true;
            }
        }
        return revised;
    }

    public static void main(String[] args) {
        // Example CSP with constraints and domains
        int[][] constraints = {
                {0, 2, 3},
                {-2, 0, 1},
                {-3, -1, 0}
        };

        List<Integer>[] domains = new ArrayList[3];
        domains[0] = new ArrayList<>(Arrays.asList(1, 2, 3));
        domains[1] = new ArrayList<>(Arrays.asList(1, 2, 3));
        domains[2] = new ArrayList<>(Arrays.asList(1, 2, 3));

        // Create an instance of CSP
        CSP csp = new CSP(constraints, domains);

        // Make the CSP arc-consistent
        csp.makeArcConsistent();

        // Print the updated domains after arc consistency propagation
        for (int i = 0; i < domains.length; i++) {
            System.out.println("Domain of variable " + i + ": " + domains[i]);
        }
    }
}

