import javafx.application.Platform;
import java.util.concurrent.Callable;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.FutureTask;

public class JavaFxInvokeAndWaitExample {

    public static <T> T invokeAndWait(Callable<T> callable) throws InterruptedException, ExecutionException {
        if (Platform.isFxApplicationThread()) {
            // If already in JavaFX thread, directly call the callable
            try {
                return callable.call();
            } catch (Exception e) {
                throw new ExecutionException("Exception in callable", e);
            }
        } else {
            // If not in JavaFX thread, use FutureTask to run the callable in JavaFX thread
            FutureTask<T> futureTask = new FutureTask<>(callable);
            Platform.runLater(futureTask);
            try {
                // Wait for the callable to complete
                return futureTask.get();
            } catch (Exception e) {
                throw new ExecutionException("Exception in callable", e);
            }
        }
    }

    public static void main(String[] args) {
        // Example usage
        try {
            invokeAndWait(() -> {
                // Callable to be executed in JavaFX thread
                // Your code logic here
                System.out.println("Running in JavaFX thread");
                return null;
            });
        } catch (InterruptedException | ExecutionException e) {
            e.printStackTrace();
        }
    }
}

