        String icon = "";
        if (progressPercentage == 0) {
            icon = "Icon for 0% progress";
        } else if (progressPercentage >= 1 && progressPercentage <= 25) {
            icon = "Icon for 1-25% progress";
        } else if (progressPercentage >= 26 && progressPercentage <= 50) {
            icon = "Icon for 26-50% progress";
        } else if (progressPercentage >= 51 && progressPercentage <= 75) {
            icon = "Icon for 51-75% progress";
        } else {
            icon = "Icon for 76-100% progress";
        }

        System.out.println("Progress Percentage: " + progressPercentage + "%");
        System.out.println("Icon: " + icon);
    }

    public static void main(String[] args) {
        // Example usage
        displayProgressIcon(0);
        displayProgressIcon(25);
