import com.github.javaparser.JavaParser;
import com.github.javaparser.ParserConfiguration;
import com.github.javaparser.ast.CompilationUnit;
import com.github.javaparser.ast.Node;
import com.github.javaparser.ast.visitor.VoidVisitorAdapter;
import com.github.javaparser.language.Language;
import com.github.javaparser.language.LanguageVersion;
import com.github.javaparser.language.LanguageVersionHandler;
import com.github.javaparser.utils.ParserCollectionStrategy;
import com.github.javaparser.utils.Pair;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.util.List;
import java.util.Map;
import java.util.Optional;
import java.util.Set;

public class DefaultLanguageVersionExample {

    public static void main(String[] args) throws IOException {
        File sourceFile = new File("path/to/your/source/file.java");
        LanguageVersion defaultLanguageVersion = getDefaultLanguageVersion(sourceFile);
        System.out.println("Default Language Version: " + defaultLanguageVersion);
    }

    public static LanguageVersion getDefaultLanguageVersion(File sourceFile) throws IOException {
        FileInputStream fis = new FileInputStream(sourceFile);
        CompilationUnit cu = JavaParser.parse(fis);
        fis.close();

        LanguageVersion defaultLanguageVersion = null;

        // Get the first Language of the source file
        Optional<Language> firstLanguage = cu.getNodesByType(Node.class).stream()
                .flatMap(node -> node.getAllContainedNodes().stream())
                .filter(node -> node instanceof LanguageVersionHandler)
                .map(node -> ((LanguageVersionHandler) node).getLanguageVersion().getLanguage())
                .findFirst();

        if (firstLanguage.isPresent()) {
            Language language = firstLanguage.get();

            // Get the default LanguageVersion for the Language
            Map<Language, Set<LanguageVersion>> defaultLanguageVersions = ParserCollectionStrategy.getDefaultLanguageVersions();
            if (defaultLanguageVersions.containsKey(language)) {
                Set<LanguageVersion> languageVersions = defaultLanguageVersions.get(language);
                if (!languageVersions.isEmpty()) {
                    defaultLanguageVersion = languageVersions.iterator().next();
                }
            }
        }

        return defaultLanguageVersion;
    }
}

