public static int decodeIntegerFromFormatString(String format, int index, boolean advance) {
    int length = format.length();
    int value = 0;

    // Check if the character at the specified index is a digit
    if (index >= 0 && index < length && Character.isDigit(format.charAt(index))) {
        // Decode the integer value
        while (index < length && Character.isDigit(format.charAt(index))) {
            value = (value * 10) + Character.getNumericValue(format.charAt(index));
            index++;
        }

        // Advance the stream position if the 'advance' flag is set to true
        if (advance) {
            index++;
        }
    }

    return value;
}

