import io.jsonwebtoken.*;
import java.util.Date;

public class JwtIssuerExample {
    private static final String SECRET_KEY = "mySecretKey123";

    public static void main(String[] args) {
        String token = createJwt("John Doe", null);
        System.out.println("JWT token: " + token);
        try {
            verifyJwt(token);
        } catch (Exception e) {
            System.out.println("Error verifying JWT: " + e.getMessage());
        }
    }

    private static String createJwt(String subject, String issuer) {
        long nowMillis = System.currentTimeMillis();
        Date now = new Date(nowMillis);

        JwtBuilder builder = Jwts.builder()
                .setSubject(subject)
                .setIssuedAt(now)
                .setExpiration(new Date(nowMillis + 3600000)) // expires in 1 hour
                .signWith(SignatureAlgorithm.HS256, SECRET_KEY);
        if (issuer != null) {
            builder.setIssuer(issuer);
        }
        return builder.compact();
    }

    private static void verifyJwt(String token) throws Exception {
        Jwts.parserBuilder().setSigningKey(SECRET_KEY).build().parseClaimsJws(token);
        System.out.println("JWT verified successfully");
    }
