import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.URL;
import java.net.URLEncoder;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class EntrezGeneIDExample {

    public static String getEntrezGeneID(String geneSymbol) throws Exception {
        String baseURL = "https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi";
        String db = "gene";
        String term = URLEncoder.encode(geneSymbol, "UTF-8");

        String url = baseURL + "?db=" + db + "&term=" + term;
        URL apiURL = new URL(url);

        BufferedReader in = new BufferedReader(new InputStreamReader(apiURL.openStream()));
        String inputLine;
        StringBuilder response = new StringBuilder();

        while ((inputLine = in.readLine()) != null) {
            response.append(inputLine);
        }

        in.close();

        Pattern idPattern = Pattern.compile("<Id>(\\d+)</Id>");
        Matcher idMatcher = idPattern.matcher(response.toString());

        if (idMatcher.find()) {
            return idMatcher.group(1);
        } else {
            return null;
        }
    }

    public static void main(String[] args) throws Exception {
        String geneSymbol = "Gene A";
        String entrezGeneID = getEntrezGeneID(geneSymbol);
        System.out.println("Entrez Gene ID for " + geneSymbol + ": " + entrezGeneID);
    }
}

