import com.google.maps.DirectionsApi;
import com.google.maps.DirectionsApiRequest;
import com.google.maps.DirectionsResult;
import com.google.maps.GeoApiContext;
import com.google.maps.model.LatLng;

public Double getTravelDistance(LatLng location1, LatLng location2, String apiKey) {
    // Set up a GeoApiContext object with the API key
    GeoApiContext context = new GeoApiContext.Builder()
        .apiKey(apiKey)
        .build();

    // Create a DirectionsApiRequest object for the two locations
    DirectionsApiRequest request = new DirectionsApiRequest(context)
        .origin(location1)
        .destination(location2);

    try {
        // Execute the request and get the DirectionsResult
        DirectionsResult result = request.await();

        // Check if there is at least one route
        if (result.routes != null && result.routes.length > 0) {
            // Get the distance of the first route
            return result.routes[0].legs[0].distance.inMeters / 1000.0;
        }

    } catch (Exception e) {
        // Handle any exceptions
        e.printStackTrace();
    }

    // Return null if the two locations are not linked by a connection
    return null;
}

