public static Element getKeyInfoFromEncryptedData(Document document, String encryptedDataId) throws Exception {
    XPath xPath = XPathFactory.newInstance().newXPath();
    String expression = String.format("//*[@Id='%s']", encryptedDataId);
    Node encryptedDataNode = (Node) xPath.evaluate(expression, document, XPathConstants.NODE);
    if (!(encryptedDataNode instanceof Element)) {
        throw new Exception("Node with Id " + encryptedDataId + " is not an Element");
    }
    EncryptedData encryptedData = XMLCipher.getInstance().loadEncryptedData(document, (Element) encryptedDataNode);
    KeyInfo keyInfo = encryptedData.getKeyInfo();
    if (keyInfo == null) {
        throw new Exception("KeyInfo is not present in EncryptedData with Id " + encryptedDataId);
    }
    Element keyInfoElement = keyInfo.getElement();
    if (keyInfoElement == null) {
        throw new Exception("KeyInfo element is not present in EncryptedData with Id " + encryptedDataId);
    }
    return keyInfoElement;
}

