    public static boolean isValidCovarianceMatrix(double[][] covarianceMatrix) {
        int numRows = covarianceMatrix.length;
        int numCols = covarianceMatrix[0].length;

        if (numRows != numCols) {
            // Covariance matrix must be square
            return false;
        }

        for (int i = 0; i < numRows; i++) {
            if (covarianceMatrix[i][i] <= 0) {
                // Diagonal elements must be positive
                return false;
            }
        }

        // If no invalid conditions are found, then the covariance matrix is considered valid
        return true;
    }

