        double[][] invalidCovarianceMatrix = {{1.0, -0.5, 0.3}, {-0.5, 2.0, 0.2}, {0.3, 0.2, 3.0}};

        boolean isValid1 = isValidCovarianceMatrix(validCovarianceMatrix);
