        if (value < 0 || value > 255) {
            throw new IllegalArgumentException("Value must be between 0 and 255 inclusive");
        }

        // Convert the unsigned byte value to a byte and put it into the buffer
        buffer.put((byte) (value & 0xFF));
    }

