        int retrievedUnsignedByte = retrievedByte & 0xFF; // Convert signed byte to unsigned byte

        System.out.println("Original unsigned byte: " + unsignedByte);
        System.out.println("Retrieved unsigned byte: " + retrievedUnsignedByte);
        System.out.println("Are they equal? " + (unsignedByte == retrievedUnsignedByte));
