public class ViewConverter {
    private static final double MIN_LATITUDE = -90.0; // Minimum valid latitude
    private static final double MAX_LATITUDE = 90.0; // Maximum valid latitude
    private double viewHeight; // Height of the view in pixels

    public ViewConverter(double viewHeight) {
        // Initialize the view height
        this.viewHeight = viewHeight;
    }

    public double computeLatitude(double yCoordinate) {
        // Calculate the corresponding latitude using linear interpolation
        double fraction = yCoordinate / viewHeight;
        double latitude = MIN_LATITUDE + fraction * (MAX_LATITUDE - MIN_LATITUDE);
        return latitude;
    }

    public static void main(String[] args) {
        // Example usage
        double viewHeight = 800.0; // View height in pixels
        ViewConverter viewConverter = new ViewConverter(viewHeight);

        double yCoordinate = 400.0; // Example view Y coordinate
        double latitude = viewConverter.computeLatitude(yCoordinate);

        System.out.println("Latitude for view Y coordinate " + yCoordinate + " is: " + latitude);
    }
}

