public class MatrixDeterminantCalculator {
    public static int calculateDeterminant(int[][] matrix) {
        int n = matrix.length;
        int[] indices = new int[n];
        for (int i = 0; i < n; i++) {
            indices[i] = i;
        }
        return calculateDeterminant(matrix, indices);
    }

    private static int calculateDeterminant(int[][] matrix, int[] indices) {
        int n = matrix.length;
        if (n == 1) {
            return matrix[0][0];
        }
        int determinant = 0;
        int sign = 1;
        for (int i = 0; i < n; i++) {
            int[] subIndices = getSubIndices(indices, i);
            determinant += sign * matrix[i][0] * calculateDeterminant(matrix, subIndices);
            sign *= -1;
        }
        return determinant;
    }

    private static int[] getSubIndices(int[] indices, int excludeIndex) {
        int n = indices.length;
        int[] subIndices = new int[n - 1];
        int j = 0;
        for (int i = 0; i < n; i++) {
            if (i != excludeIndex) {
                subIndices[j++] = indices[i];
            }
        }
        return subIndices;
    }

    public static void main(String[] args) {
        int[][] matrix = {
                {1, 2, 3},
                {4, 5, 6},
                {7, 8, 9}
        };

        int determinant = calculateDeterminant(matrix);
        System.out.println("Determinant: " + determinant);
    }
}

