import java.beans.BeanInfo;
import java.beans.IntrospectionException;
import java.beans.Introspector;
import java.beans.PropertyDescriptor;
import java.util.ArrayList;
import java.util.List;

public class PropertyDescriptorUtils {

    // Method to create a copied list of property descriptors
    public static List<PropertyDescriptor> getCopyOfPropertyDescriptors(Class<?> beanClass) {
        List<PropertyDescriptor> copiedList = new ArrayList<>();

        try {
            // Get the BeanInfo for the given bean class
            BeanInfo beanInfo = Introspector.getBeanInfo(beanClass);

            // Get the array of property descriptors from the BeanInfo
            PropertyDescriptor[] propertyDescriptors = beanInfo.getPropertyDescriptors();

            // Loop through each property descriptor and create a copy of it
            for (PropertyDescriptor pd : propertyDescriptors) {
                // Create a new PropertyDescriptor object with the same properties
                PropertyDescriptor copiedPd = new PropertyDescriptor(pd.getName(), pd.getReadMethod(),
                        pd.getWriteMethod());

                // Add the copied property descriptor to the list
                copiedList.add(copiedPd);
            }
        } catch (IntrospectionException e) {
            // Handle the exception as appropriate for your application
            e.printStackTrace();
        }

        return copiedList;
    }

    // ... other code ...
}

