public class UnitConverter {
    // Conversion factors for different units
    private static final float MILES_TO_KILOMETERS = 1.60934f;
    private static final float POUNDS_TO_KILOGRAMS = 0.453592f;
    // Add more conversion factors for other units as needed

    // Method to convert a float value from one unit to another
    public static float convert(float value, Unit sourceUnit, Unit targetUnit) {
        if (sourceUnit == null || targetUnit == null) {
            throw new IllegalArgumentException("Source and target units must be specified");
        }

        if (sourceUnit == targetUnit) {
            return value; // No conversion needed
        }

        float conversionFactor = getConversionFactor(sourceUnit, targetUnit);
        if (conversionFactor == 0.0f) {
            throw new IllegalArgumentException("Conversion not supported from " +
                    sourceUnit.name() + " to " + targetUnit.name());
        }

        return value * conversionFactor;
    }

    // Helper method to get the conversion factor between two units
    private static float getConversionFactor(Unit sourceUnit, Unit targetUnit) {
        switch (sourceUnit) {
            case MILES:
                switch (targetUnit) {
                    case KILOMETERS:
                        return MILES_TO_KILOMETERS;
                }
                break;
            case POUNDS:
                switch (targetUnit) {
                    case KILOGRAMS:
                        return POUNDS_TO_KILOGRAMS;
                }
                break;
            // Add more cases for other unit conversions as needed
        }

        return 0.0f; // If no conversion factor found
    }

    // Enum to represent different units
    public enum Unit {
        MILES,
        KILOMETERS,
        POUNDS,
        KILOGRAMS
        // Add more units as needed
    }
}

