            // Step 3: Use the default value
            if (wsdlFactory == null) {
                wsdlFactory = WSDLFactory.newInstance();
            }

            // Once an instance of WSDLFactory is obtained, you can use it to create desired instances
            if (wsdlFactory != null) {
