public class GroupMentionExample {

    // Represents a group entity
    static class Group {
        private String groupId;
        private List<String> mentions;

        public Group(String groupId) {
            this.groupId = groupId;
            this.mentions = new ArrayList<>();
        }

        public String getGroupId() {
            return groupId;
        }

        public void addMention(String mentionedUserId) {
            mentions.add(mentionedUserId);
        }

        public List<String> getMentions() {
            return mentions;
        }
    }

    // Retrieve groups in which a user was mentioned
    public static List<Group> getGroupsUserWasMentioned(String userId, List<Group> groups) {
        List<Group> groupsUserWasMentioned = new ArrayList<>();
        for (Group group : groups) {
            if (group.getMentions().contains(userId)) {
                groupsUserWasMentioned.add(group);
            }
        }
        return groupsUserWasMentioned;
    }

    public static void main(String[] args) {
        // Sample groups data
        Group group1 = new Group("group1");
        group1.addMention("user1");
        group1.addMention("user2");

        Group group2 = new Group("group2");
        group2.addMention("user1");
        group2.addMention("user3");

        Group group3 = new Group("group3");
        group3.addMention("user2");
        group3.addMention("user4");

        List<Group> groups = new ArrayList<>();
        groups.add(group1);
        groups.add(group2);
        groups.add(group3);

        String userId = "user1";
        List<Group> groupsUserWasMentioned = getGroupsUserWasMentioned(userId, groups);

        System.out.println("Groups in which user " + userId + " was mentioned:");
