import java.beans.BeanInfo;
import java.beans.IntrospectionException;
import java.beans.PropertyDescriptor;
import java.beans.Introspector;
import java.lang.reflect.InvocationTargetException;

public class PropertyDescriptionExample {

    // Define a sample object with properties
    static class SampleObject {
        private String name;
        private int age;
        private boolean active;

        // Getters and Setters
        // ...
    }

    // Method to retrieve the description of a property
    public static String getPropertyDescription(String propertyName, Object object) throws IntrospectionException,
            IllegalAccessException, InvocationTargetException {
        BeanInfo beanInfo = Introspector.getBeanInfo(object.getClass());
        PropertyDescriptor[] propertyDescriptors = beanInfo.getPropertyDescriptors();
        for (PropertyDescriptor descriptor : propertyDescriptors) {
            if (descriptor.getName().equals(propertyName)) {
                return descriptor.getShortDescription();
            }
        }
        return null;
    }

    public static void main(String[] args) {
        SampleObject sampleObject = new SampleObject();
        sampleObject.setName("John");
        sampleObject.setAge(30);
        sampleObject.setActive(true);

        String propertyName = "name";
        try {
            String propertyDescription = getPropertyDescription(propertyName, sampleObject);
            if (propertyDescription != null) {
                System.out.println("Description of property '" + propertyName + "': " + propertyDescription);
            } else {
                System.out.println("No description found for property '" + propertyName + "'");
            }
        } catch (IntrospectionException | IllegalAccessException | InvocationTargetException e) {
            e.printStackTrace();
        }
    }
}

