import java.math.BigDecimal;
import java.math.RoundingMode;

public class SatoshisToBTCExample {
    public static void main(String[] args) {
        // Example value in satoshis
        long satoshis = 150000;

        // Convert satoshis to BTC
        String btc = convertSatoshisToBTC(satoshis);
        System.out.println("Value in BTC: " + btc);
    }

    public static String convertSatoshisToBTC(long satoshis) {
        // Convert satoshis to BigDecimal with 8 decimal places
        BigDecimal btcValue = new BigDecimal(satoshis).setScale(8);

        // Divide by 100 million to get BTC value
        btcValue = btcValue.divide(new BigDecimal("100000000"), RoundingMode.DOWN);

        // Convert BigDecimal to plain string without trailing zeroes
        String btc = btcValue.stripTrailingZeros().toPlainString();

        return btc;
    }
}

