public ImageSize getRenderedImageSize(Document document, int maxWidth, int maxHeight) {
    // Get the layout dimensions of the document
    int layoutWidth = document.getLayoutWidth();
    int layoutHeight = document.getLayoutHeight();

    // Calculate the scaling factor for width and height
    double scaleX = (double) maxWidth / layoutWidth;
    double scaleY = (double) maxHeight / layoutHeight;

    // Determine the scaling factor to be used
    double scaleFactor = Math.min(scaleX, scaleY);

    // Calculate the rendered image width and height based on the scaling factor
    int renderedWidth = (int) (layoutWidth * scaleFactor);
    int renderedHeight = (int) (layoutHeight * scaleFactor);

    // Create and return an ImageSize object with the calculated dimensions
    return new ImageSize(renderedWidth, renderedHeight);
}

