    public static InputStream findFile(String fileName) throws IOException {
        // Attempt to find file as a local file
        File file = new File(fileName);
        if (file.exists()) {
            return new FileInputStream(file);
        }

        // Attempt to find file as a URL
        URL url = new URL(fileName);
        InputStream urlInputStream = url.openStream();
        if (urlInputStream != null) {
            return urlInputStream;
        }

        // Attempt to find file on the classpath
        InputStream classpathInputStream = FileFinder.class.getClassLoader().getResourceAsStream(fileName);
        if (classpathInputStream != null) {
            return classpathInputStream;
        }

        // File not found
