import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;

public class BitcoinHashUtil {
    // Define a method to calculate the hash of the hash of the contents
    public static byte[] calculateDoubleHash(byte[] data) throws NoSuchAlgorithmException {
        // Create a SHA-256 message digest for the first hash
        MessageDigest firstDigest = MessageDigest.getInstance("SHA-256");
        byte[] firstHash = firstDigest.digest(data);

        // Create a SHA-256 message digest for the second hash
        MessageDigest secondDigest = MessageDigest.getInstance("SHA-256");
        byte[] doubleHash = secondDigest.digest(firstHash);

        // Return the double hash
        return doubleHash;
    }

    public static void main(String[] args) throws NoSuchAlgorithmException {
