public class Example {
    public static byte[] createScriptSig(byte[] signature, byte[] publicKey) {
        // If signature is null, create an incomplete scriptSig with OP_0 instead of signature
        if (signature == null) {
            byte[] incompleteScriptSig = {0x00};
            return incompleteScriptSig;
        }

        // Create the complete scriptSig
        byte[] signatureLength = {(byte) signature.length};
        byte[] publicKeyLength = {(byte) publicKey.length};
        List<byte[]> scriptSigParts = Arrays.asList(signatureLength, signature, publicKeyLength, publicKey);
        byte[] scriptSig = concatByteArrays(scriptSigParts);
        return scriptSig;
    }

    public static byte[] concatByteArrays(List<byte[]> byteArrays) {
        int totalLength = 0;
        for (byte[] byteArray : byteArrays) {
            totalLength += byteArray.length;
        }
        byte[] result = new byte[totalLength];
        int currentIndex = 0;
        for (byte[] byteArray : byteArrays) {
            System.arraycopy(byteArray, 0, result, currentIndex, byteArray.length);
            currentIndex += byteArray.length;
        }
        return result;
    }
}

