import org.json.JSONArray;
import org.json.JSONObject;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import java.util.ArrayList;
import java.util.List;

public class JSONArrayToNodeListConverter {
    public static List<Node> convertToNodeList(JSONArray jsonArray) throws ParserConfigurationException {
        List<Node> nodeList = new ArrayList<>();
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();
        Document document = builder.newDocument();
        Element rootElement = document.createElement("root");
        document.appendChild(rootElement);

        for (int i = 0; i < jsonArray.length(); i++) {
            JSONObject jsonObject = jsonArray.getJSONObject(i);
            Element element = document.createElement(jsonObject.getString("name"));
            rootElement.appendChild(element);
            for (String key : jsonObject.keySet()) {
                if (!key.equals("name")) {
                    element.setAttribute(key, jsonObject.getString(key));
                }
            }
            nodeList.add(element);
        }

        return nodeList;
    }
}

