public class RandomColorGenerator {

    private final static float TINT = 0.7f;
    private final static Random RANDOM = new Random();

    public static void main(String[] args) {
        String[] keys = {"key1", "key2", "key3", "key4", "key5"};

        Map<String, Color> colorMap = buildRandomColorMap(keys);

        for (String key : colorMap.keySet()) {
            System.out.println(key + ": " + colorMap.get(key));
        }
    }

    public static Map<String, Color> buildRandomColorMap(String[] keys) {
        Map<String, Color> colorMap = new HashMap<>();
        for (String key : keys) {
            Color color = getRandomColor();
            color = tintColor(color, TINT);
            colorMap.put(key, color);
        }
        return colorMap;
    }

    private static Color getRandomColor() {
        int r = RANDOM.nextInt(256);
        int g = RANDOM.nextInt(256);
        int b = RANDOM.nextInt(256);
        return new Color(r, g, b);
    }

    private static Color tintColor(Color color, float tint) {
        int r = (int) (color.getRed() * tint);
        int g = (int) (color.getGreen() * tint);
        int b = (int) (color.getBlue() * tint);
        return new Color(r, g, b);
    }
}

