import org.apache.tika.mime.MimeTypes;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.util.Optional;

public class LanguageDetection {

    public static Optional<String> detectLanguage(File file) throws IOException {
        byte[] bytes = Files.readAllBytes(file.toPath());
        MediaType mediaType = MimeTypes.getDefaultMimeTypes().detect(bytes);
        if (mediaType == null || !mediaType.getType().equals("text")) {
            // File is not text, so language cannot be detected
            return Optional.empty();
        }
        LanguageIdentifier identifier = new LanguageIdentifier(new String(bytes));
        return Optional.of(identifier.getLanguage());
    }

