public void runOnFxThreadAndWait(Runnable runnable, long timeout) throws TimeoutException, InterruptedException {
    if (Platform.isFxApplicationThread()) {
        throw new IllegalStateException("This method cannot be called from the JavaFX Application Thread");
    }

    CountDownLatch latch = new CountDownLatch(1);

    Platform.runLater(() -> {
        try {
            runnable.run();
        } finally {
            latch.countDown();
        }
    });

    if (!latch.await(timeout, TimeUnit.MILLISECONDS)) {
        throw new TimeoutException("Timed out waiting for Runnable to complete");
    }
}

