import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;

public class LineReader {
    public void onLineComplete(String line) {
        // This method will be called when a line is complete
        System.out.println("Line complete: " + line);
    }

    public void readLines(String filename) throws IOException {
        BufferedReader reader = new BufferedReader(new FileReader(filename));

        String line;
        StringBuilder sb = new StringBuilder();

        while ((line = reader.readLine()) != null) {
            sb.append(line);
            if (line.endsWith("\n")) {
                // A line is complete
                String completeLine = sb.toString();
                onLineComplete(completeLine);
                sb.setLength(0); // Reset the string builder
            }
        }

        reader.close();
    }

    public static void main(String[] args) throws IOException {
        LineReader lineReader = new LineReader();
        lineReader.readLines("input.txt");
    }
}

