import org.apache.commons.math3.linear.*;

public class QRQualityExample {

    public static double computeQuality(RealMatrix qr) {
        int n = qr.getColumnDimension();
        double[] rDiag = qr.getDiagonal();
        double rNorm = MatrixUtils.createRealDiagonalMatrix(rDiag).getNorm();
        double qrNorm = qr.getNorm();
        double tol = Math.max(n, qrNorm) * Math.ulp(rNorm);
        double rTrace = qr.getTrace();
        double quality = 0;
        if (Math.abs(rTrace) > tol) {
            quality = rNorm / (qrNorm * Math.sqrt(1 - Math.pow(rTrace / (qrNorm * rNorm), 2)));
        }
        return quality;
    }

    public static void main(String[] args) {
        double[][] matrixData = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
        RealMatrix matrix = MatrixUtils.createRealMatrix(matrixData);
        QRDecomposition qr = new QRDecomposition(matrix);
        double quality = computeQuality(qr.getR());
        System.out.println("QR quality: " + quality);
    }
}

