import com.xuggle.xuggler.IAudioSamples;
import com.xuggle.xuggler.IContainer;
import com.xuggle.xuggler.IPacket;
import com.xuggle.xuggler.IStream;
import com.xuggle.xuggler.IStreamCoder;
import com.xuggle.xuggler.io.IURLProtocolHandler;
import com.xuggle.xuggler.io.URLProtocolManager;
import java.nio.ByteBuffer;

public class Muxer {

    private final IContainer container;
    private final IPacket packet;
    private final IStream stream;
    private final IStreamCoder coder;
    private final IAudioSamples samples;

    public Muxer(String fileName) {
        URLProtocolManager.registerProtocol(new IURLProtocolHandler() {
            @Override
            public int fill(Buffer buf, int size) throws IOException {
                return 0;
            }

            @Override
            public int open(String url, int flags) throws IOException {
                return 0;
            }

            @Override
            public int read(Buffer buf, int size) throws IOException {
                return 0;
            }

            @Override
            public long seek(long offset, int whence) throws IOException {
                return 0;
            }

            @Override
            public int write(Buffer buf, int size) throws IOException {
                return 0;
            }
        });

        container = IContainer.make();
        int result = container.open(fileName, IContainer.Type.WRITE, null);
        if (result < 0) {
            throw new RuntimeException("Could not open container for output.");
        }

        int audioStreamId = container.addNewStream(0);
        if (audioStreamId < 0) {
            throw new RuntimeException("Could not add audio stream to container.");
        }

        stream = container.getStream(audioStreamId);
        coder = stream.getStreamCoder();

        coder.setCodec(ICodec.findEncodingCodecByName("mp3"));
        coder.setSampleRate(44100);
        coder.setChannels(2);
        coder.setBitRate(128000);

        result = coder.open(null, null);
        if (result < 0) {
            throw new RuntimeException("Could not open coder for encoding.");
        }

        packet = IPacket.make();
        samples = IAudioSamples.make(512, coder.getChannels(), IAudioSamples.Format.FMT_S32);
    }

    public void write(ByteBuffer data) {
        samples.getData().put(data);
        while (samples.getTimeStamp() < container.getGlobalTimeBase().convertTo(stream.getTimeBase(), stream.getTimeCodeBase(), 1000000L)) {
            int result = coder.encodeAudio(packet, samples);
            if (result < 0) {
                throw new RuntimeException("Could not encode audio.");
            }
            if (packet.isComplete()) {
                container.write(packet, true);
            }
        }
    }

    public void close() {
        while (coder.encodeAudio(packet, null) >= 0) {
            if (packet.isComplete()) {
                container.write(packet, true);
            }
        }
        container.close();
    }
}

