public QName parseQualifiedName(String s, NamespaceContext nsContext) throws IllegalArgumentException {
    String[] parts = s.split(":");
    if (parts.length == 2) {
        // prefix-version
        String prefix = parts[0];
        String localName = parts[1];
        String namespaceURI = nsContext.getNamespaceURI(prefix);
        if (namespaceURI == null) {
            throw new IllegalArgumentException("Namespace prefix not defined: " + prefix);
        }
        return new QName(namespaceURI, localName, prefix);
    } else if (parts.length == 1) {
        // {NS-URI}LOCAL-NAME
        if (!s.startsWith("{") || !s.contains("}")) {
            throw new IllegalArgumentException("Invalid QName format: " + s);
        }
        String namespaceURI = s.substring(1, s.indexOf("}"));
        String localName = s.substring(s.indexOf("}") + 1);
        return new QName(namespaceURI, localName);
    } else {
        throw new IllegalArgumentException("Invalid QName format: " + s);
    }
}

