import java.util.Stack;

public class TagCloser {
    public static String closeTags(String input) {
        Stack<String> tagStack = new Stack<>();
        StringBuilder sb = new StringBuilder();

        for (int i = 0; i < input.length(); i++) {
            if (input.charAt(i) == '<') {
                // Start of a tag
                int endIndex = input.indexOf('>', i + 1);
                if (endIndex != -1) {
                    String tag = input.substring(i, endIndex + 1);
                    if (tag.startsWith("</")) {
                        // Closing tag
                        if (!tagStack.isEmpty() && tagStack.peek().equals(tag.substring(2))) {
                            sb.append(tag);
                            tagStack.pop();
                        }
                    } else {
                        // Opening tag
                        sb.append(tag);
                        tagStack.push(tag.substring(1));
                    }
                    i = endIndex;
                } else {
                    // Invalid tag, treat as regular character
                    sb.append(input.charAt(i));
                }
            } else {
                // Regular character
                sb.append(input.charAt(i));
            }
        }

        // Close any remaining open tags
        while (!tagStack.isEmpty()) {
            sb.append("</").append(tagStack.pop()).append(">");
        }

        return sb.toString();
    }

    public static void main(String[] args) {
        String input = "<html><head><title>Test</title></head><body><p>Hello, world!</p>";
        String output = closeTags(input);
        System.out.println("Input: " + input);
        System.out.println("Output: " + output);
    }
}

