        throw new NumberFormatException("radix " + radix +
                                           " greater than Character.MAX_RADIX");
    }
    int result = 0;
    int len = s.length();
    for (int i = 0; i < len; i++) {
        int digit = Character.digit(s.charAt(i), radix);
        if (digit < 0) {
            throw new NumberFormatException(s);
        }
        result = result * radix + digit;
    }
    return result;
}

