import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.attribute.AclEntry;
import java.nio.file.attribute.AclEntryPermission;
import java.nio.file.attribute.AclFileAttributeView;
import java.nio.file.attribute.UserPrincipal;
import java.util.List;
import java.util.Set;

public class FileAccessPermissionChecker {

    public static boolean hasPermission(Path path, UserPrincipal user, Set<AclEntryPermission> permissions) throws Exception {
        if (!Files.exists(path)) {
            return true; // non-existent paths are always accessible
        }

        AclFileAttributeView aclView = Files.getFileAttributeView(path, AclFileAttributeView.class);
        if (aclView == null) {
            return false; // no ACL view for this file system
        }

        List<AclEntry> aclEntries = aclView.getAcl();
        for (AclEntry entry : aclEntries) {
            if (entry.principal().equals(user) && entry.permissions().containsAll(permissions)) {
                return true;
            }
        }

        return false;
    }
}

