import java.util.List;
import java.util.PriorityQueue;

public class TopKSelector<T extends Comparable<T>> {

    private PriorityQueue<T> minHeap;
    private int k;

    public TopKSelector(int k) {
        this.minHeap = new PriorityQueue<>();
        this.k = k;
    }

    public void addCandidates(List<T> elements) {
        for (T element : elements) {
            if (minHeap.size() < k) {
                // If the heap is not full, add the element directly
                minHeap.offer(element);
            } else if (minHeap.peek().compareTo(element) < 0) {
                // If the heap is full and the new element is greater than the minimum element
                // in the heap, remove the minimum element and add the new element
                minHeap.poll();
                minHeap.offer(element);
            }
        }
    }

