public class SingularValueDecompositionExample {

    public static void main(String[] args) {
        // Create a sample matrix
        double[][] data = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
        RealMatrix matrix = MatrixUtils.createRealMatrix(data);

        // Perform singular value decomposition
        SingularValueDecomposition svd = new SingularValueDecomposition(matrix);

        // Get the singular values in descending order
        double[] singularValues = svd.getSingularValues();
        for (int i = 0; i < singularValues.length; i++) {
            for (int j = 0; j < singularValues.length; j++) {
                if (i == j) {
                    // Create a diagonal matrix with the singular values
                    double[][] diagonalMatrix = new double[singularValues.length][singularValues.length];
                    diagonalMatrix[i][j] = singularValues[i];
                    RealMatrix diagonal = MatrixUtils.createRealMatrix(diagonalMatrix);
                    System.out.println(diagonal.toString());
                }
            }
        }
    }
}

