import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import java.util.function.Function;

public class TopKSelector<T> {

    private final List<T> topK;
    private final Function<T, Double> scoreFunction;
    private final int k;

    public TopKSelector(int k, Function<T, Double> scoreFunction) {
        this.topK = new ArrayList<>(k);
        this.scoreFunction = scoreFunction;
        this.k = k;
    }

    public void addAll(Iterable<? extends T> elements) {
        for (T element : elements) {
            add(element);
        }
    }

    public void add(T element) {
        double score = scoreFunction.apply(element);
        if (topK.size() < k || score > scoreFunction.apply(topK.get(topK.size() - 1))) {
            topK.add(element);
            Collections.sort(topK, (a, b) -> Double.compare(scoreFunction.apply(b), scoreFunction.apply(a)));
            if (topK.size() > k) {
                topK.remove(topK.size() - 1);
            }
        }
    }

    public List<T> getTopK() {
        return Collections.unmodifiableList(new ArrayList<>(topK));
    }
}

