public Individual getBestIndividual(Population population, FitnessFunction fitnessFn, int numIterations) {
    Individual bestIndividual = null;
    double bestFitness = Double.NEGATIVE_INFINITY;

    for (int i = 0; i < numIterations; i++) {
        // Evaluate fitness of each individual in the population
        for (Individual individual : population.getIndividuals()) {
            double fitness = fitnessFn.calculateFitness(individual);
            individual.setFitness(fitness);

            // Update best individual if this one has better fitness
            if (fitness > bestFitness) {
                bestIndividual = individual;
                bestFitness = fitness;
            }
        }

        // Create a new generation of individuals using selection, crossover, and mutation
        population.createNextGeneration();
    }

