    public void addItem(ItemStack itemStack) {
        for (int i = 0; i < items.length; i++) {
            if (items[i] == null) {
                items[i] = itemStack;
                break;
            } else if (items[i].isSimilar(itemStack)) {
                int amount = items[i].getAmount() + itemStack.getAmount();
                if (amount <= items[i].getMaxStackSize()) {
                    items[i].setAmount(amount);
                    break;
                } else {
                    int remaining = amount - items[i].getMaxStackSize();
                    items[i].setAmount(items[i].getMaxStackSize());
                    itemStack.setAmount(remaining);
                }
            }
        }
    }
}

