public class DomainMatch {
    public static boolean domainMatch(String hostA, String hostB) {
        // Check if the two host names are identical
        if (hostA.equals(hostB)) {
            return true;
        }
        // Check if hostA is a HDN string of the form NB, where N is a non-empty name string
        if (hostA.contains(".") && !hostA.startsWith(".") && !hostA.endsWith(".")) {
            int dotIndex = hostA.indexOf(".");
            String name = hostA.substring(0, dotIndex);
            String domain = hostA.substring(dotIndex);
            // Check if domain is a HDN string of the form .B', where B' is a HDN string
            if (domain.startsWith(".") && isHdnString(domain.substring(1))) {
                // Construct the matching domain
                String matchingDomain = "." + domain.substring(1);
                // Check if hostB ends with the matching domain
                return hostB.endsWith(matchingDomain);
            }
        }
        // Otherwise, the host names do not match
        return false;
    }

    private static boolean isHdnString(String s) {
        // A HDN string is a non-empty sequence of labels separated by dots
        String[] labels = s.split("\\.");
        for (String label : labels) {
            // Each label must be non-empty and consist only of letters, digits, or hyphens
            if (label.isEmpty() || !label.matches("[a-zA-Z0-9\\-]+")) {
                return false;
            }
        }
        return true;
    }
}

