public class ReverseLogIterator implements Iterable<LogRecord> {

    private final List<LogRecord> records;

    public ReverseLogIterator(List<LogRecord> records) {
        this.records = records;
    }

    @Override
    public Iterator<LogRecord> iterator() {
        // Return an iterator that starts with the last log record in the list
        return new Iterator<LogRecord>() {
            private int index = records.size() - 1;

            @Override
            public boolean hasNext() {
                return index >= 0;
            }

            @Override
            public LogRecord next() {
                return records.get(index--);
            }
        };
    }
}

